mapper.select:
/*commoncdDao.commonCodelistSearch*/ SELECT fc.code_id , fc.code_field , fc.code_name , fc.code_eng_name , fc.code_type , fc.remark , fc.module_id , fc.use_yn FROM fnd_code fc inner join fnd_module fm on fc.module_id = fm.module_id where 1=1 AND fc.company_id = #{companyId}
and fm.module_id = #{moduleId}
and (fc.code_field LIKE CONCAT('%', #{keyword}, '%')
OR fc.code_name LIKE CONCAT('%', #{keyword}, '%') OR fc.code_eng_name LIKE CONCAT('%', #{keyword}, '%'))
order by code_id, code_field

mapper.insert:
/*commoncdDao.insertCodeDetailComAdd*/
INSERT into fnd_code_detail
    ( code_detail_id
    , code_id
    , company_id
    , defined_cd
    , defined_name
    , defined_eng_name
    , use_yn
    , value01
    , value02
    , value03
    , defined_type
    , created_by
    , creation_date
    , last_updated_by
    , last_update_date)
    VALUES ( SEEDIT.nextval('fnd_code_detail')
           , #{codeId}
           , #{companyId}
           , #{definedCd}
           , #{definedName}
           , #{definedEngName}
           , #{useYn}
           , #{value01}
           , #{value02}
           , #{value03}
           , 'COM'
           , #{lastUpdatedBy}
           , now()
           , #{lastUpdatedBy}
           , now()
           )

mapper.update:
/*commoncdDao.codeDetailComUpdate*/
UPDATE fnd_code_detail
SET company_id = #{companyId}
  , defined_cd = #{definedCd}
  , defined_name = #{definedName}
  , defined_eng_name = #{definedEngName}
  , use_yn = #{useYn}
  , value01 = #{value01}
  , value02 = #{value02}
  , value03 = #{value03}
  , last_updated_by = #{lastUpdatedBy}
  , last_update_date = now()
WHERE code_detail_id = #{codeDetailId}
