mapper.select:
SELECT base_yyyymm
	, company_id
	, employee_number
	, last_request_email_id
	, last_status
	, last_updated_date
	, last_success_date
FROM hr_dh_paystubmail
WHERE company_id = #{companyId}
AND base_yyyymm           = #{baseYyyymm}        
AND employee_number       = #{employeeNumber}    
AND last_request_email_id = #{lastRequestEmailId}
AND last_status           = #{lastStatus}        
ORDER BY base_yyyymm, companyId, employeeNumber DESC

mapper.insert:
INSERT INTO hr_dh_paystubmail (base_yyyymm, company_id, employee_number
	 , last_request_email_id 
	 , last_success_date     
) VALUES( #{baseYyyymm}, #{companyId}, #{employeeNumber}
	 , #{lastRequestEmailId} 
	 , #{lastSuccessDate}    
) ON DUPLICATE KEY UPDATE

	 , last_request_email_id = #{lastRequestEmailId} 
	 , last_status           = #{lastStatus}         
	 , last_updated_date     = #{lastUpdatedDate}

mapper.update:
INSERT INTO hr_dh_paystubmail (base_yyyymm, company_id, employee_number
	 , last_request_email_id 
	 , last_success_date     
) VALUES( #{baseYyyymm}, #{companyId}, #{employeeNumber}
	 , #{lastRequestEmailId} 
	 , #{lastSuccessDate}    
) ON DUPLICATE KEY UPDATE

	 , last_request_email_id = #{lastRequestEmailId} 
	 , last_status           = #{lastStatus}         
	 , last_updated_date     = #{lastUpdatedDate}    
	 , last_success_date     = #{lastSuccessDate}

mapper.delete:
DELETE FROM hr_dh_paystubmail
WHERE base_yyyymm = #{baseYyyymm}
AND company_id = #{companyId}      
AND employee_number = #{employeeNumber}
