mapper.select:
/*SalaryDao.findCompanyList*/ SELECT company_id AS value ,company_name AS name FROM fnd_company WHERE 1=1
AND company_id = #{companyId}
ORDER BY company_id

/*SalaryDao.findCompanyList*/ SELECT 999 as value , "전체" as name UNION ALL SELECT est_id AS value ,est_name AS name FROM fnd_business_est WHERE 1=1
AND company_id = #{companyId}
ORDER BY CASE WHEN value = 999 THEN 1 ELSE 2 END, value

/*SalaryDao.findAdtList*/ SELECT hadt.work_date -- 근태기준일 , fbe.est_name -- 사업장 , hd.department_name -- 부서 , he.employee_number -- 사원번호 , he.korean_name -- 성명 , fcd02.defined_name -- 직급 , hadt.work_status -- 근무형태 , hadt.work_start_date -- 출근일시 , hadt.work_end_date -- 퇴근일시 , hadt.in_status -- 출근판정 , hadt.out_status -- 퇴근판정 , hadt.out_start_date , hadt.out_end_date , TIME_FORMAT(TIMEDIFF(hadt.out_end_date, hadt.out_start_date ), '%H:%i') AS out_time , hadt.late_time -- 지각 , hadt.over_time -- 연장근무기간 , hadt.night_time -- 야간근무시간 , hadt.holiday_time -- 휴일 , hadt.real_work_time -- 실제근무시간 , hadt.default_time -- 정상근무시간 , fcd01.defined_name -- 급여구분 FROM hr_employee he INNER JOIN hr_department hd ON he.company_id = hd.company_id AND he.department_id = hd.department_id AND he.attribute1 = hd.attribute2 -- 급여구분 INNER JOIN fnd_code fc01 ON fc01.company_id = he.company_id AND fc01.code_field = 'HR_A0002' INNER JOIN fnd_code_detail fcd01 ON fcd01.company_id = he.company_id AND fcd01.code_id = fc01.code_id AND fcd01.value01 = he.employee_type -- 직급 INNER JOIN fnd_code fc02 ON fc02.company_id = he.company_id AND fc02.code_field = 'HR_A0003' INNER JOIN fnd_code_detail fcd02 ON fcd02.company_id = he.company_id AND fcd02.code_id = fc02.code_id AND fcd02.defined_cd = he.position_code -- 사업장 INNER JOIN fnd_business_est fbe ON fbe.company_id = he.company_id AND fbe.est_id = he.est_id INNER JOIN hr_dh_adt_manage hadt ON hadt.company_id = he.company_id AND hadt.employee_id = he.employee_id AND hadt.attribute1 = he.attribute1 WHERE he.company_id = #{companyId}
AND he.est_id = #{estId}
AND he.attribute1 = #{yyyymm} ORDER BY he.korean_name, hadt.work_date

/*SalaryDao.getCalcSalaryList*/ SELECT he.employee_number , hdcs.salary_id , he.korean_name , fbe.est_name , hd.department_name , fcd02.defined_name , he.employee_type , hdcs.basic_salary , ifnull(hdcs.attribute01,0)+ifnull(hdcs.attribute15,0) AS annual_allowance , hdcs.attribute02 AS overtime_allowance01 , hdcs.attribute03 AS overtime_allowance02 , ifnull(hdcs.attribute04,0) + ifnull(hdcs.attribute18,0) AS night_allowance01 , hdcs.attribute05 AS night_allowance02 , hdcs.attribute06 AS holiday_allowance01 , hdcs.attribute07 AS holiday_allowance02 , hdcs.attribute08 AS position_allowance , hdcs.attribute09 AS otherAllowance01 , hdcs.attribute16 AS otherAllowance02 , hdcs.attribute10 AS subsidies , hdcs.attribute11 AS transportation_expenses , hdcs.attribute12 AS meals_expenses , IFNULL(hb.bonus_amount,0) AS bonus_amount , IFNULL(hdcs.basic_salary,0) + (ifnull(hdcs.attribute01,0)+ifnull(hdcs.attribute15,0)) + IFNULL(hdcs.attribute02,0) + IFNULL(hdcs.attribute03,0) + (ifnull(hdcs.attribute04,0) + ifnull(hdcs.attribute18,0)) + IFNULL(hdcs.attribute05,0) + IFNULL(hdcs.attribute06,0) + IFNULL(hdcs.attribute07,0) + IFNULL(hdcs.attribute08,0) + IFNULL(hdcs.attribute09,0) + IFNULL(hdcs.attribute10,0) + IFNULL(hdcs.attribute11,0) + IFNULL(hdcs.attribute12,0) + IFNULL(hb.bonus_amount,0) AS salary_sum FROM hr_dh_calc_salary hdcs INNER JOIN hr_employee he ON hdcs.company_id = he.company_id AND hdcs.employee_id = he.employee_id AND hdcs.yyyymm = he.attribute1 INNER JOIN fnd_business_est fbe ON hdcs.company_id = fbe.company_id AND he.est_id = fbe.est_id INNER JOIN hr_department hd ON he.company_id = hd.company_id AND he.department_id = hd.department_id AND he.attribute1 = hd.attribute2 INNER JOIN fnd_code fc02 ON fc02.company_id = he.company_id AND fc02.code_field = 'HR_A0003' INNER JOIN fnd_code_detail fcd02 ON fcd02.company_id = he.company_id AND fcd02.code_id = fc02.code_id AND fcd02.defined_cd = he.position_code LEFT JOIN hr_bonus hb ON he.company_id = hb.company_id AND he.employee_id = hb.employee_id AND he.attribute1 = hb.yyyymm WHERE he.company_id = #{companyId}
AND he.est_id = #{estId}
AND he.korean_name LIKE CONCAT('%', #{koreanName}, '%')
AND he.attribute1 = #{yyyymm} union all SELECT "합계" as employee_number , 0000 as salary_id , "" as korean_name , "" as est_name , "" as department_name , "" as defined_name , "" as employee_type , sum(hdcs.basic_salary) as basic_salary , sum(ifnull(hdcs.attribute01,0)) + sum(ifnull(hdcs.attribute15,0)) AS annual_allowance , sum(hdcs.attribute02) AS overtime_allowance01 , sum(hdcs.attribute03) AS overtime_allowance02 , sum(ifnull(hdcs.attribute04,0)) + sum(ifnull(hdcs.attribute18,0)) AS night_allowance01 , sum(hdcs.attribute05) AS night_allowance02 , sum(hdcs.attribute06) AS holiday_allowance01 , sum(hdcs.attribute07) AS holiday_allowance02 , sum(hdcs.attribute08) AS position_allowance , sum(hdcs.attribute09) AS otherAllowance01 , sum(hdcs.attribute16) AS otherAllowance02 , sum(hdcs.attribute10) AS subsidies , sum(hdcs.attribute11) AS transportation_expenses , sum(hdcs.attribute12) AS meals_expenses , sum(IFNULL(hb.bonus_amount,0)) AS bonus_amount , sum(IFNULL(hdcs.basic_salary,0)) + sum(ifnull(hdcs.attribute01,0)) + sum(ifnull(hdcs.attribute15,0)) + sum(IFNULL(hdcs.attribute02,0)) + sum(IFNULL(hdcs.attribute03,0)) + sum(ifnull(hdcs.attribute04,0)) + sum(ifnull(hdcs.attribute18,0)) + sum(IFNULL(hdcs.attribute05,0)) + sum(IFNULL(hdcs.attribute06,0)) + sum(IFNULL(hdcs.attribute07,0)) + sum(IFNULL(hdcs.attribute08,0)) + sum(IFNULL(hdcs.attribute09,0)) + sum(IFNULL(hdcs.attribute10,0)) + sum(IFNULL(hdcs.attribute11,0)) + sum(IFNULL(hdcs.attribute12,0)) + sum(IFNULL(hb.bonus_amount,0)) AS salary_sum FROM hr_dh_calc_salary hdcs INNER JOIN hr_employee he ON hdcs.company_id = he.company_id AND hdcs.employee_id = he.employee_id AND hdcs.yyyymm = he.attribute1 INNER JOIN fnd_business_est fbe ON hdcs.company_id = fbe.company_id AND he.est_id = fbe.est_id LEFT JOIN hr_bonus hb ON he.company_id = hb.company_id AND he.employee_id = hb.employee_id AND he.attribute1 = hb.yyyymm WHERE he.company_id = #{companyId} AND he.attribute1 = #{yyyymm}
AND he.est_id = #{estId}
AND he.korean_name LIKE CONCAT('%', #{koreanName}, '%')
group by he.company_id, he.attribute1
, fbe.est_id
, he.korean_name LIKE CONCAT('%', #{koreanName}, '%')
ORDER BY CASE WHEN salary_id =000 THEN 1 ELSE 2 END, employee_type, employee_number

mapper.update:
/*SalaryDao.upadateOtherAllowance*/
	UPDATE hr_dh_calc_salary hdcs
	INNER JOIN hr_employee he
		ON hdcs.company_id  = he.company_id
		AND hdcs.employee_id  = he.employee_id
		AND hdcs.yyyymm  = he.attribute1
	SET	hdcs.attribute16 = (IFNULL(hdcs.attribute16,0) + #{otherAllowance02})
		,hdcs.last_updated_by = #{loginUserId}
		,hdcs.last_update_date = now()
	WHERE	he.employee_number = #{employeeNumber}
	AND		hdcs.yyyymm = #{yyyymm}

