/*ReportDao.findERPIUData*/ SELECT fc.attribute1 AS cd_company -- 회사코드 , fbe.attribute1 AS cd_bizarea -- 사업장코드 , hdcs.yyyymm AS ym -- 년월 , case when he.employment_code='099' then '001' else he.employment_code end AS cd_emp -- 고용형태 , case when he.duty_type = '201' then '100' else he.employee_type end AS tp_emp -- 사원구분 , '001' AS tp_pay -- 급여구분 , 1 AS no_seq -- 순번 , he.employee_number AS no_emp -- 사번 , ifnull(hdcs.basic_salary,0) AS am_pay01 -- 기본급 , ifnull(hdcs.attribute01,0)+ifnull(hdcs.attribute15,0)+ifnull(hdcs.attribute23,0) AS am_pay02 -- 연차수당 , ifnull(hdcs.attribute02,0) AS am_pay03 -- 연장수당1 , ifnull(hdcs.attribute03,0) AS am_pay04 -- 연장수당2 , ifnull(hdcs.attribute04,0) + ifnull(hdcs.attribute18,0) AS am_pay05 -- 야간수당1 , ifnull(hdcs.attribute05,0) AS am_pay06 -- 야간수당2 , ifnull(hdcs.attribute06,0) AS am_pay07 -- 휴일수당1 , ifnull(hdcs.attribute07,0) AS am_pay08 -- 휴일수당2 , ifnull(hdcs.attribute08,0) AS am_pay09 -- 직책수당 , ifnull(hdcs.attribute09,0)+ifnull(hdcs.attribute16,0) AS am_pay10 -- 기타수당 , ifnull(hdcs.attribute10,0) AS am_pay11 -- 보조금 , ifnull(hdcs.attribute11,0) AS am_pay12 -- 교통비 , ifnull(hdcs.attribute12,0) AS am_pay13 -- 식대 , CASE WHEN IFNULL(fcd06.defined_cd, '99999') = '100' THEN 0 ELSE 1 END as check_first , CASE WHEN IFNULL(fcd06.defined_cd, '99999') = '201' THEN 1 ELSE 0 END as check_special FROM hr_dh_calc_salary hdcs INNER JOIN fnd_company fc ON hdcs.company_id = fc.company_id INNER JOIN fnd_business_est fbe ON hdcs.company_id = fbe.company_id INNER JOIN hr_employee he ON hdcs.company_id = he.company_id AND hdcs.employee_id = he.employee_id AND hdcs.yyyymm = he.attribute1 AND he.est_id = fbe.est_id -- 직급 LEFT JOIN fnd_code fc02 ON fc02.company_id = he.company_id AND fc02.code_field = 'HR_A0003' LEFT JOIN fnd_code_detail fcd02 ON fcd02.company_id = he.company_id AND fcd02.code_id = fc02.code_id AND fcd02.defined_cd = he.position_code -- 직위순서 LEFT JOIN fnd_code fc07 ON fc07.company_id = he.company_id AND fc07.code_field = 'HR_A0007' INNER JOIN fnd_code_detail fcd07 ON fcd07.company_id = fc07.company_id AND fcd07.code_id = fc07.code_id AND fcd07.defined_cd = he.position_code -- 직군 LEFT JOIN fnd_code fc06 ON fc06.company_id = he.company_id AND fc06.code_field = 'HR_A0006' LEFT JOIN fnd_code_detail fcd06 ON fcd06.company_id = he.company_id AND fcd06.code_id = fc06.code_id AND fcd06.defined_cd = he.duty_type LEFT JOIN hr_department hd ON hd.department_id = he.department_id AND hdcs.company_id = hd.company_id WHERE fc.company_id = #{companyId}
AND he.est_id = #{estId}
AND hdcs.yyyymm = #{yyyymm} ORDER BY check_first -- 임원 , he.est_id -- 사업장 , LPAD(IFNULL(hd.order_index,'9999999999'), '10' , '0') -- 부서정렬 , check_special , LPAD(IFNULL(fcd07.value01,'9999999999'), '10' , '0') -- 직급정렬 , he.employee_number -- 사번

/*ReportDao.findPayrollData*/ SELECT he.employee_id , he.employee_number , he.korean_name , fbe.est_name , hd.department_name , fcd02.defined_name , CASE WHEN IFNULL(fcd06.defined_cd, '99999') = '100' THEN 0 ELSE 1 END as check_first , CASE WHEN IFNULL(fcd06.defined_cd, '99999') = '201' THEN 1 ELSE 0 END as check_special , he.employee_type , he.hire_date , IF(he.retire_date != '9999-12-31', he.retire_date, null) , IFNULL(hdcs.basic_salary,0) AS basic_salary , ifnull(hdcs.attribute01,0)+ifnull(hdcs.attribute15,0)+ifnull(hdcs.attribute23,0) AS annual_allowance , IFNULL(hdcs.attribute02,0) AS overtime_allowance01 , IFNULL(hdcs.attribute03,0) AS overtime_allowance02 , ifnull(hdcs.attribute04,0) + ifnull(hdcs.attribute18,0) AS night_allowance01 , IFNULL(hdcs.attribute05,0) AS night_allowance02 , IFNULL(hdcs.attribute06,0) AS holiday_allowance01 , IFNULL(hdcs.attribute07,0) AS holiday_allowance02 , IFNULL(hdcs.attribute08,0) AS position_allowance , IFNULL(hdcs.attribute09,0)+ifnull(hdcs.attribute16,0) AS other_allowances , IFNULL(hdcs.attribute10,0) AS subsidies , IFNULL(hdcs.attribute11,0) AS transportation_expenses , IFNULL(hdcs.attribute12,0) AS meals_expenses , 0 AS bonus_amount , IFNULL(hdcs.basic_salary,0) + ifnull(hdcs.attribute01,0)+ifnull(hdcs.attribute15,0)+ifnull(hdcs.attribute23,0) + IFNULL(hdcs.attribute02,0) + IFNULL(hdcs.attribute03,0) + ifnull(hdcs.attribute04,0) + ifnull(hdcs.attribute18,0) + IFNULL(hdcs.attribute05,0) + IFNULL(hdcs.attribute06,0) + IFNULL(hdcs.attribute07,0) + IFNULL(hdcs.attribute08,0) + IFNULL(hdcs.attribute09,0)+ifnull(hdcs.attribute16,0) + IFNULL(hdcs.attribute10,0) + IFNULL(hdcs.attribute11,0) + IFNULL(hdcs.attribute12,0) AS salary_sum , IFNULL(hdt.AM_DEDUCT01,0) AS national_pension , IFNULL(hdt.AM_DEDUCT02,0) AS health_insurance , IFNULL(hdt.AM_DEDUCT03,0) AS care_insurance , IFNULL(hdt.AM_DEDUCT04,0) AS employment_insurance , IFNULL(hdt.AM_DEDUCT05,0) AS advance , IFNULL(hdt.AM_DEDUCT06,0) AS other_tax , IFNULL(hdt.AM_DEDUCT07,0) AS gyeongjobi , IFNULL(hdt.AM_DEDUCT08,0) + IFNULL(hdt.AM_DEDUCT09,0) AS yearend , IFNULL(hdt.AM_DEDUCT10,0) AS health_insurance_settlement , IFNULL(hdt.AM_DEDUCT11,0) AS care_insurance_settlement , IFNULL(hdt.AM_DEDUCT12,0) AS holiday_tax , IFNULL(hdt.AM_INCOMTAX,0) AS incomtax , IFNULL(hdt.AM_RESIDTAX,0) AS residtax , IFNULL(hdt.AM_DEDUCT01,0) + IFNULL(hdt.AM_DEDUCT02,0) + IFNULL(hdt.AM_DEDUCT03,0) + IFNULL(hdt.AM_DEDUCT04,0) + IFNULL(hdt.AM_DEDUCT05,0) + IFNULL(hdt.AM_DEDUCT06,0) + IFNULL(hdt.AM_DEDUCT07,0) + IFNULL(hdt.AM_DEDUCT08,0) + IFNULL(hdt.AM_DEDUCT09,0) + IFNULL(hdt.AM_DEDUCT10,0) + IFNULL(hdt.AM_DEDUCT11,0) + IFNULL(hdt.AM_DEDUCT12,0) + IFNULL(hdt.AM_INCOMTAX,0) + IFNULL(hdt.AM_RESIDTAX,0) AS tax_sum , IFNULL(hdt.DT_PAY,'00000000') AS dt_pay FROM hr_dh_calc_salary hdcs INNER JOIN fnd_business_est fbe ON hdcs.company_id = fbe.company_id INNER JOIN hr_employee he ON hdcs.company_id = he.company_id AND hdcs.employee_id = he.employee_id AND hdcs.yyyymm = he.attribute1 AND he.est_id = fbe.est_id -- 부서 INNER JOIN hr_department hd ON he.company_id = hd.company_id AND he.department_id = hd.department_id AND he.attribute1 = hd.attribute2 -- 직급 INNER JOIN fnd_code fc02 ON fc02.company_id = he.company_id AND fc02.code_field = 'HR_A0003' INNER JOIN fnd_code_detail fcd02 ON fcd02.company_id = he.company_id AND fcd02.code_id = fc02.code_id AND fcd02.defined_cd = he.position_code -- 직위순서 LEFT JOIN fnd_code fc07 ON fc07.company_id = he.company_id AND fc07.code_field = 'HR_A0007' INNER JOIN fnd_code_detail fcd07 ON fcd07.company_id = fc07.company_id AND fcd07.code_id = fc07.code_id AND fcd07.defined_cd = he.position_code -- 직군 LEFT JOIN fnd_code fc06 ON fc06.company_id = he.company_id AND fc06.code_field = 'HR_A0006' LEFT JOIN fnd_code_detail fcd06 ON fcd06.company_id = he.company_id AND fcd06.code_id = fc06.code_id AND fcd06.defined_cd = he.duty_type -- AND fcd06.defined_cd = 100 -- 임원 -- -- 상여 -- LEFT JOIN hr_bonus hb -- ON he.company_id = hb.company_id -- AND he.employee_id = hb.employee_id -- AND he.attribute1 = hb.yyyymm -- 세금 LEFT JOIN hr_dh_tax hdt ON he.company_id = hdt.company_id AND he.employee_id = hdt.employee_id AND he.attribute1 = hdt.yyyymm AND hdt.tp_pay = '001' WHERE he.company_id = #{companyId}
AND he.est_id = #{estId}
AND he.attribute1 = #{yyyymm} ORDER BY check_first -- 임원 , he.est_id -- 사업장 , LPAD(IFNULL(hd.order_index,'9999999999'), '10' , '0') -- 부서정렬 , check_special , LPAD(IFNULL(fcd07.value01,'9999999999'), '10' , '0') -- 직급정렬 , he.employee_number -- 사번

SELECT he.employee_id , he.employee_number , he.korean_name , fbe.est_name , hd.department_name , fcd02.defined_name , CASE WHEN IFNULL(fcd06.defined_cd, '99999') = '100' THEN 0 ELSE 1 END as check_first , CASE WHEN IFNULL(fcd06.defined_cd, '99999') = '201' THEN 1 ELSE 0 END as check_special , he.employee_type , he.hire_date , IF(he.retire_date != '9999-12-31', he.retire_date, null) , IFNULL(hb.bonus_amount ,0) AS basic_salary , 0 AS annual_allowance , 0 AS overtime_allowance01 , 0 AS overtime_allowance02 , 0 AS night_allowance01 , 0 AS night_allowance02 , 0 AS holiday_allowance01 , 0 AS holiday_allowance02 , 0 AS position_allowance , 0 AS other_allowances , 0 AS subsidies , 0 AS transportation_expenses , 0 AS meals_expenses , 0 AS bonus_amount , IFNULL(hb.bonus_amount,0) AS salary_sum , IFNULL(hdt.AM_DEDUCT01,0) AS national_pension , IFNULL(hdt.AM_DEDUCT02,0) AS health_insurance , IFNULL(hdt.AM_DEDUCT03,0) AS care_insurance , IFNULL(hdt.AM_DEDUCT04,0) AS employment_insurance , IFNULL(hdt.AM_DEDUCT05,0) AS advance , IFNULL(hdt.AM_DEDUCT06,0) AS other_tax , IFNULL(hdt.AM_DEDUCT07,0) AS gyeongjobi , IFNULL(hdt.AM_DEDUCT08,0) + IFNULL(hdt.AM_DEDUCT09,0) AS yearend , IFNULL(hdt.AM_DEDUCT10,0) AS health_insurance_settlement , IFNULL(hdt.AM_DEDUCT11,0) AS care_insurance_settlement , IFNULL(hdt.AM_DEDUCT12,0) AS holiday_tax , IFNULL(hdt.AM_INCOMTAX,0) AS incomtax , IFNULL(hdt.AM_RESIDTAX,0) AS residtax , IFNULL(hdt.AM_DEDUCT01,0) + IFNULL(hdt.AM_DEDUCT02,0) + IFNULL(hdt.AM_DEDUCT03,0) + IFNULL(hdt.AM_DEDUCT04,0) + IFNULL(hdt.AM_DEDUCT05,0) + IFNULL(hdt.AM_DEDUCT06,0) + IFNULL(hdt.AM_DEDUCT07,0) + IFNULL(hdt.AM_DEDUCT08,0) + IFNULL(hdt.AM_DEDUCT09,0) + IFNULL(hdt.AM_DEDUCT10,0) + IFNULL(hdt.AM_DEDUCT11,0) + IFNULL(hdt.AM_DEDUCT12,0) + IFNULL(hdt.AM_INCOMTAX,0) + IFNULL(hdt.AM_RESIDTAX,0) AS tax_sum , IFNULL(hdt.DT_PAY,'00000000') AS dt_pay FROM hr_bonus hb INNER JOIN fnd_business_est fbe ON hb.company_id = fbe.company_id INNER JOIN hr_employee he ON hb.company_id = he.company_id AND hb.employee_id = he.employee_id AND hb.yyyymm = he.attribute1 AND he.est_id = fbe.est_id -- 부서 INNER JOIN hr_department hd ON he.company_id = hd.company_id AND he.department_id = hd.department_id AND he.attribute1 = hd.attribute2 -- 직급 INNER JOIN fnd_code fc02 ON fc02.company_id = he.company_id AND fc02.code_field = 'HR_A0003' INNER JOIN fnd_code_detail fcd02 ON fcd02.company_id = he.company_id AND fcd02.code_id = fc02.code_id AND fcd02.defined_cd = he.position_code -- 직위순서 LEFT JOIN fnd_code fc07 ON fc07.company_id = he.company_id AND fc07.code_field = 'HR_A0007' INNER JOIN fnd_code_detail fcd07 ON fcd07.company_id = fc07.company_id AND fcd07.code_id = fc07.code_id AND fcd07.defined_cd = he.position_code -- 직군 LEFT JOIN fnd_code fc06 ON fc06.company_id = he.company_id AND fc06.code_field = 'HR_A0006' LEFT JOIN fnd_code_detail fcd06 ON fcd06.company_id = he.company_id AND fcd06.code_id = fc06.code_id AND fcd06.defined_cd = he.duty_type -- AND fcd06.defined_cd = 100 -- 임원 -- 세금 LEFT JOIN hr_dh_tax hdt ON he.company_id = hdt.company_id AND he.employee_id = hdt.employee_id AND he.attribute1 = hdt.yyyymm AND hdt.tp_pay = '002' WHERE he.company_id = #{companyId}
AND he.est_id = #{estId}
AND he.attribute1 = #{yyyymm} ORDER BY check_first -- 임원 , he.est_id -- 사업장 , LPAD(IFNULL(hd.order_index,'9999999999'), '10' , '0') -- 부서정렬 , check_special , LPAD(IFNULL(fcd07.value01,'9999999999'), '10' , '0') -- 직급정렬 , he.employee_number -- 사번

/*ReportDao.findPersonalPayroll*/ SELECT hadt.work_date -- 근태기준일 , fbe.est_name -- 사업장 , hd.department_name -- 부서 , he.employee_number -- 사원번호 , he.korean_name -- 성명 , fcd02.defined_name -- 직급 , hadt.work_status -- 근무형태 , hadt.work_start_date -- 출근일시 , hadt.work_end_date -- 퇴근일시 , hadt.in_status -- 출근판정 , hadt.out_status -- 퇴근판정 , hadt.out_start_date , hadt.out_end_date , TIME_FORMAT(TIMEDIFF(hadt.out_end_date, hadt.out_start_date ), '%H:%i') AS out_time , hadt.late_time -- 지각 , hadt.over_time -- 연장근무기간 , hadt.night_time -- 야간근무시간 , hadt.holiday_time -- 휴일 , hadt.real_work_time -- 실제근무시간 , hadt.default_time -- 정상근무시간 , fcd01.defined_name -- 급여구분 FROM hr_employee he INNER JOIN hr_department hd ON he.company_id = hd.company_id AND he.department_id = hd.department_id AND he.attribute1 = hd.attribute2 -- 급여구분 INNER JOIN fnd_code fc01 ON fc01.company_id = he.company_id AND fc01.code_field = 'HR_A0002' INNER JOIN fnd_code_detail fcd01 ON fcd01.company_id = he.company_id AND fcd01.code_id = fc01.code_id AND fcd01.value01 = he.employee_type -- 직급 INNER JOIN fnd_code fc02 ON fc02.company_id = he.company_id AND fc02.code_field = 'HR_A0003' INNER JOIN fnd_code_detail fcd02 ON fcd02.company_id = he.company_id AND fcd02.code_id = fc02.code_id AND fcd02.defined_cd = he.position_code -- 사업장 INNER JOIN fnd_business_est fbe ON fbe.company_id = he.company_id AND fbe.est_id = he.est_id INNER JOIN hr_dh_adt_manage hadt ON hadt.company_id = he.company_id AND hadt.employee_id = he.employee_id AND hadt.attribute1 = he.attribute1 WHERE he.company_id = #{companyId}
AND he.est_id = #{estId}
AND he.attribute1 = #{yyyymm} AND he.employee_id = #{employeeId} AND hadt.work_date >= he.hire_date AND hadt.work_date <= he.retire_date ORDER BY hadt.work_date
